import React from 'react'
import { useState, useEffect } from 'react';
import styles from "./MainSpace.module.css"
import logo from "./assets/logoFood.svg"
import axios from 'axios';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import openai from "openai"

import {TextInput} from '@gravity-ui/uikit';

const style_modal = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  bgcolor: '#fff',
  boxShadow: 24,
  p: 4,
  display: "flex",
  flexDirection: "column",
  gap: 5,
  borderRadius: 4
};



export default function MainSpace() {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const [response, setResponse] = useState('');
  const [prompt, setPrompt] = useState('');

  const handleSubmit = async (e) => {
    url = 'https://openai80.p.rapidapi.com/completions'
    e.preventDefault();
    try {
      const options = {
        method: 'POST',
        url: 'https://openai80.p.rapidapi.com/completions',
        headers: {
          'content-type': 'application/json',
          'X-RapidAPI-Key': '4c0913e8fbmshee3d5797b276796p1dabc3jsnd83be6ecfb8b',
          'X-RapidAPI-Host': 'openai80.p.rapidapi.com'
        },
        data: {
          model: 'text-davinci-003',
          prompt: prompt,
          max_tokens: 7,
          temperature: 0,
          top_p: 1,
          n: 1,
          stream: false,
          logprobs: null,
          stop: ''
        }
      };

      const response = await fetch(url, options);
      const result = await response.text();
      console.log(result);
      // Set the API response to state or perform other actions with it
      setResponse(result);
    } catch (error) {
      console.error(error);
    }
  };




  return (
    <>
      <section className={styles.main_space}>
        <div className={styles.content_part}>
            <div className={styles.block_content}>
                <img className={styles.logo} src={logo} alt="" />
            </div>
            <div className={styles.block_content}>
                <span className={styles.secondary_txt}>Вкусное искусство выбора</span>
            </div>
            <div className={styles.block_content}>
              <button onClick={handleOpen} className={styles.btn}>
                Выбрать свой рецепт
              </button>
            </div>
        </div>
      </section>
      <Modal
        open={open}
        onClose={handleClose}
      >
        <Box sx={style_modal}>
          <span className={styles.modal_title}>Намекните бы вы хотели приготовить?</span>
          <form onSubmit={handleSubmit}>
              <input type="text" className={styles.input} value={prompt} onChange={e => setPrompt(e.target.value)} />
              <button type="submit">Submit</button>
          </form>
        </Box>
      </Modal>
    </>
  )
}
