import styles from "./ResultPage.module.css"
import React, {useState, useEffect} from "react"

import { useSelector } from 'react-redux';
import { useLocation } from 'react-router-dom';

import axios from 'axios';

export default function ResultPage() {

    const [title, setTitle] = useState("");
    const [ingredients, setIngredients] = useState("");
    const [instructions, setInstructions] = useState("");
    const [index, setIndex] = useState(0);
    const [max, setMax] = useState(0);
    const [array, setArray] = useState([]);

    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const query = queryParams.get('query');


    const options = {
        method: 'GET',
        url: 'https://recipe-by-api-ninjas.p.rapidapi.com/v1/recipe',
        params: {
          query: query
        },
        headers: {
          'X-RapidAPI-Key': '4c0913e8fbmshee3d5797b276796p1dabc3jsnd83be6ecfb8b',
          'X-RapidAPI-Host': 'recipe-by-api-ninjas.p.rapidapi.com'
        }
    };

    const getData = async () => {
        try {
            const response = await axios.request(options);
            setArray(response.data);
        } catch (error) {
            console.error(error);
        }
    }


    const back = () => {
        if (index > 0) {
            setIndex(index - 1);
            setTitle(array[index - 1].title);
            setIngredients(array[index - 1].ingredients);
            setInstructions(array[index - 1].instructions);
        }
    }

    const next = () => {
        if (index < test.length - 1) {
            setIndex(index + 1);
            setTitle(array[index + 1].title);
            setIngredients(array[index + 1].ingredients);
            setInstructions(array[index + 1].instructions);
        }
    }

    useEffect(() => {
        getData();

        setMax(array.length);
        console.log(max)
        setTitle(array[index].title);
        setIngredients(array[index].ingredients);
        setInstructions(array[index].instructions);
    }, [index])

    useEffect(() => {
    }, [])

    return (
        <>
            {
                index === 0 ?

                (
                    <header className={styles.header}>
                        <button className={styles.nav_button} onClick={back}>
                            <span></span>
                            <span className={styles.button_title}></span>
                        </button>

                        <h1>{title}</h1>

                        <button className={styles.nav_button} onClick={next}>
                            <span className={styles.button_title}>Next</span>
                            <span>→</span>
                        </button>
                    </header>
                )
                :
                (
                    <header className={styles.header}>
                            <button className={styles.nav_button} onClick={back}>
                                <span>←</span>
                                <span className={styles.button_title} >Previous</span>
                            </button>

                            <h1>{title}</h1>

                            <button className={styles.nav_button} onClick={next}>
                                <span className={styles.button_title} >Next</span>
                                <span>→</span>
                            </button>
                    </header> 
                )
            }

            <section className={styles.content}>
                <div className={styles.content_block}>
                    <p>{ingredients}</p>
                </div>
                <div className={styles.content_block}>
                    <h1>Instructions</h1>
                    <p>{instructions}</p>
                </div>
            </section>
        </>
    )
}
