import { createSlice } from '@reduxjs/toolkit';

const listSlice = createSlice({
  name: 'list',
  initialState: [],
  reducers: {
    addList: (state, action) => {
      return [...state, ...action.payload];
    },
  },
});

export const { addList } = listSlice.actions;
export default listSlice.reducer;
