import React, { useState, useEffect } from "react";
import { useSelector } from 'react-redux';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import styles from "./ResultPage.module.css";

export default function ResultPage() {
  const [title, setTitle] = useState("");
  const [ingredients, setIngredients] = useState("");
  const [instructions, setInstructions] = useState("");
  const [index, setIndex] = useState(0);
  const [max, setMax] = useState(0);
  const [array, setArray] = useState([]);

  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const query = queryParams.get('query');

  const options = {
    method: 'GET',
    url: 'https://recipe-by-api-ninjas.p.rapidapi.com/v1/recipe',
    params: { query: query },
    headers: {
      'X-RapidAPI-Key': '4c0913e8fbmshee3d5797b276796p1dabc3jsnd83be6ecfb8b',
      'X-RapidAPI-Host': 'recipe-by-api-ninjas.p.rapidapi.com'
    }
  };

  const getData = async () => {
    try {
      const response = await axios.request(options);
      setArray(response.data);
    } catch (error) {
      console.error(error);
    }
  }

  const back = () => {
    if (index > 0) {
      setIndex(index - 1);
    }
  }

  const next = () => {
    if (index < array.length - 1) {
      setIndex(index + 1);
    }
  }

  useEffect(() => {
    getData();
  }, [query]); // Fetch data when the query changes

  useEffect(() => {
    setMax(array.length);

    if (array.length > 0) {
        if (array[index].title.length > 15) {
            setTitle(array[index].title.slice(0, 15) + "...");
        }
        setIngredients(array[index].ingredients);
        setInstructions(array[index].instructions);
    }
  }, [index, array]);

  return (
    <>
      <header className={styles.header}>
        <button className={styles.nav_button} onClick={back}>
          <span>←</span>
          <span className={styles.button_title}>Previous</span>
        </button>

        <h1>{title}</h1>

        <button className={styles.nav_button} onClick={next}>
          <span className={styles.button_title}>Next</span>
          <span>→</span>
        </button>
      </header>

      <section className={styles.content}>
        <div className={styles.content_block}>
            <h1>Ingredients</h1>
            <p>{ingredients}</p>
        </div>
        <div className={styles.content_block}>
          <h1>Instructions</h1>
          <p>{instructions}</p>
        </div>
      </section>
    </>
  )
}
