const initialState = {
    dataArray: [],
  };

  const rootReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_ARRAY_DATA':
        return { ...state, dataArray: action.payload };
      default:
        return state;
    }
  };

  export default rootReducer;
