import { configureStore } from '@reduxjs/toolkit'
import modalSlice from './actionCreators/modalSlice'
import rootReducer from './reducers/dataReducer'

export default configureStore({
  reducer: {
    modal: modalSlice,
    data: rootReducer
  }
})
